// Write.jsx
import React, { useState, useEffect } from "react";
import useParams from "react-router-dom";
import { getTodoById, addTodo, updateTodo } from "../db/db";
import "../css/Write.css";

const Write = () => {
  const { id } = useParams();
  const [todo, setTodo] = useState({ title: "", contents: "" });

  useEffect(() => {
    if (id) {
      const existingTodo = getTodoById(parseInt(id));
      if (existingTodo) {
        setTodo(existingTodo);
      }
    }
  }, [id]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setTodo((prevTodo) => ({ ...prevTodo, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (id) {
      updateTodo({ ...todo, id: parseInt(id) });
    } else {
      addTodo(todo);
    }
  };

  return (
    <div className="write-container">
      <h1>{id ? "Edit Todo" : "Add Todo"}</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Title:
          <input
            type="text"
            name="title"
            value={todo.title}
            onChange={handleChange}
            required
          />
        </label>
        <label>
          Contents:
          <textarea
            name="contents"
            value={todo.contents}
            onChange={handleChange}
            required
          />
        </label>
        <button type="submit">{id ? "Update" : "Add"}</button>
      </form>
    </div>
  );
};

export default Write;
